import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as XLSX from 'xlsx';
import './App.css'; // Aseg√∫rate de tener este archivo creado para personalizaciones

function App() {
  const [pendientes, setPendientes] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [observacion, setObservacion] = useState('');
  const [editandoId, setEditandoId] = useState(null);
  const [ocultos, setOcultos] = useState({}); // üëà Estado para ocultar grupos
  const [finalizados, setFinalizados] = useState([]);
  const [mostrarFinalizados, setMostrarFinalizados] = useState(false);


  const fetchPendientes = async () => {
    const res = await fetch('http://localhost:3001/pendientes');
    const data = await res.json();
    setPendientes(data);
  };

  useEffect(() => {
    fetchPendientes();
  }, []);

  const agregarPendiente = async () => {
    if (titulo.trim() === '') return;

    if (editandoId) {
      await fetch(`http://localhost:3001/pendientes/${editandoId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ titulo, observacion }),
      });
      setEditandoId(null);
    } else {
      await fetch('http://localhost:3001/pendientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ titulo, observacion }),
      });
    }

    setTitulo('');
    setObservacion('');
    fetchPendientes();
  };

  const eliminarPendiente = async (id) => {
    await fetch(`http://localhost:3001/pendientes/${id}`, { method: 'DELETE' });
    fetchPendientes();
  };

  const exportarExcel = () => {
    if (pendientes.length === 0) return;
    const worksheet = XLSX.utils.json_to_sheet(pendientes);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "Pendientes");
    XLSX.writeFile(workbook, "pendientes.xlsx");
  };

  const pendientesAgrupados = pendientes.reduce((acc, p) => {
    if (!acc[p.titulo]) acc[p.titulo] = [];
    acc[p.titulo].push(p);
    return acc;
  }, {});

  const toggleGrupo = (titulo) => {
    setOcultos((prev) => ({
      ...prev,
      [titulo]: !prev[titulo],
    }));
  };

  return (
    <div className="container py-5">
      <h1 className="mb-4 text-center display-5">üìù Lista de Pendientes</h1>

      {/* Formulario */}
      <div className="card shadow-sm mb-4 border-0">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-6">
              <input
                list="opciones-titulo"
                className="form-control"
                placeholder="T√≠tulo"
                value={titulo}
                onChange={(e) => setTitulo(e.target.value)}
              />
              <datalist id="opciones-titulo">
                <option value="PROYECTOS" />
                <option value="PRUEBAS" />
                <option value="PENDIENTE" />
              </datalist>
            </div>
            <div className="col-md-6">
              <textarea
                className="form-control"
                placeholder="Observaci√≥n"
                value={observacion}
                onChange={(e) => setObservacion(e.target.value)}
              />
            </div>
            <div className="col-12 d-flex gap-2">
              <button className="btn btn-primary w-100" onClick={agregarPendiente}>
                {editandoId ? 'üíæ Guardar Cambios' : '‚ûï Agregar Pendiente'}
              </button>
              <button className="btn btn-success" onClick={exportarExcel}>
                üì• Exportar a Excel
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Lista agrupada */}
      {Object.entries(pendientesAgrupados).map(([grupo, items]) => (
        <div key={grupo} className="mb-4">
          <h4
            className="text-secondary border-bottom pb-1 mb-3"
            style={{ cursor: 'pointer', userSelect: 'none' }}
            onClick={() => toggleGrupo(grupo)}
          >
            {ocultos[grupo] ? '‚ñ∂Ô∏è ' : 'üîΩ '} {grupo}
          </h4>

          {!ocultos[grupo] && (
            <div className="row">
              {items.map((p) => (
                <div className="col-md-6 col-lg-4 mb-3" key={p.id}>
                  <div className="card h-100 shadow-sm border-0">
                    <div className="card-body">
                      <h5 className="card-title text-primary fw-bold">{p.titulo}</h5>
                      <p className="card-text">{p.observacion}</p>
                    </div>
                    <div className="card-footer bg-light d-flex justify-content-between">
                      <button
                        className="btn btn-outline-secondary btn-sm"
                        onClick={() => {
                          setTitulo(p.titulo);
                          setObservacion(p.observacion);
                          setEditandoId(p.id);
                        }}
                      >
                        ‚úèÔ∏è Editar
                      </button>
                      <button
                        className="btn btn-outline-danger btn-sm"
                        onClick={() => eliminarPendiente(p.id)}
                      >
                        üóë Finalizar
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
      <div className="text-center my-4">
  <button className="btn btn-outline-dark" onClick={() => setMostrarFinalizados(!mostrarFinalizados)}>
    {mostrarFinalizados ? 'üôà Ocultar Finalizados' : '‚úÖ Ver Finalizados'}
  </button>
</div>

{mostrarFinalizados && (
  <div className="mt-4">
    <h3 className="text-success mb-3">‚úÖ Pendientes Finalizados</h3>
    <div className="row">
      {finalizados.map((f) => (
        <div className="col-md-6 col-lg-4 mb-3" key={f.id}>
          <div className="card h-100 shadow-sm border border-success">
            <div className="card-body">
              <h5 className="card-title text-success fw-bold">{f.titulo}</h5>
              <p className="card-text">{f.observacion}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
)}
    </div>
  );




}

export default App;
