import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as XLSX from 'xlsx';


function App() {
  const [pendientes, setPendientes] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [observacion, setObservacion] = useState('');
  const [editandoId, setEditandoId] = useState(null);

  const fetchPendientes = async () => {
    const res = await fetch('http://localhost:3001/pendientes');
    const data = await res.json();
    setPendientes(data);
  };

  useEffect(() => {
    fetchPendientes();
  }, []);

  const agregarPendiente = async () => {
    if (titulo.trim() === '') return;

    if (editandoId) {
      await fetch(`http://localhost:3001/pendientes/${editandoId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ titulo, observacion }),
      });
      setEditandoId(null);
    } else {
      await fetch('http://localhost:3001/pendientes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ titulo, observacion }),
      });
    }

    setTitulo('');
    setObservacion('');
    fetchPendientes();
  };

  const eliminarPendiente = async (id) => {
    await fetch(`http://localhost:3001/pendientes/${id}`, { method: 'DELETE' });
    fetchPendientes();
  };

  // Agrupar pendientes por t√≠tulo
  const pendientesAgrupados = pendientes.reduce((acc, p) => {
    if (!acc[p.titulo]) acc[p.titulo] = [];
    acc[p.titulo].push(p);
    return acc;
  }, {});

 //importar a excel
  const exportarExcel = () => {
  if (pendientes.length === 0) return;

  const worksheet = XLSX.utils.json_to_sheet(pendientes);
  const workbook = XLSX.utils.book_new();
  XLSX.utils.book_append_sheet(workbook, worksheet, "Pendientes");

  XLSX.writeFile(workbook, "pendientes.xlsx");
};


  return (
    <div className="container py-5">
      <h1 className="mb-4 text-center">üìù Lista de Pendientes</h1>

      <div className="card mb-4 shadow-sm">
        <div className="card-body">
          <div className="mb-3">
            <input
              list="opciones-titulo"
              className="form-control mb-2"
              placeholder="T√≠tulo"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
            />
            <button className="btn btn-success mb-3" onClick={exportarExcel}>
            üì• Exportar a Excel
            </button>

            <datalist id="opciones-titulo">
              <option value="PROYECTOS" />
              <option value="PRUEBAS" />
              <option value="PENDIENTE" />
            </datalist>

            <textarea
              className="form-control mb-2"
              placeholder="Observaci√≥n"
              value={observacion}
              onChange={(e) => setObservacion(e.target.value)}
            />

            <button className="btn btn-primary w-100" onClick={agregarPendiente}>
              {editandoId ? 'Actualizar Pendiente' : 'Agregar Pendiente'}
            </button>
          </div>
        </div>
      </div>

      {Object.keys(pendientesAgrupados).map((grupo) => (
        <div key={grupo} className="mb-4">
          <h4 className="text-primary">{grupo}</h4>
          <div className="row">
            {pendientesAgrupados[grupo].map((p) => (
              <div className="col-md-6 mb-3" key={p.id}>
                <div className="card h-100 shadow-sm">
                  <div className="card-body">
                    <p className="card-text">{p.observacion}</p>
                  </div>
                  <div className="card-footer d-flex justify-content-end">
                    <button
                      className="btn btn-secondary btn-sm me-2"
                      onClick={() => {
                        setTitulo(p.titulo);
                        setObservacion(p.observacion);
                        setEditandoId(p.id);
                      }}
                    >
                      Editar
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => eliminarPendiente(p.id)}
                    >
                      Eliminar
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
